import { WithFramerBreakpoints } from "unframer";
import { jsx } from "react/jsx-runtime";
var locales = [];
var defaultResponsiveVariants = {};

/** @type {function(Props): any}
 * 루트 래퍼 컴포넌트:
 * - ContextProviders를 통해 routes, framerSiteId, locale 컨텍스트 제공
 * - 실제 화면 렌더는 stdin_default2(FramerrzrydiliH)에 위임
 */
function ComponentWithRoot({ locale, ...rest }) {
	return (
		<ContextProviders
			routes={routes}
			framerSiteId={
				"3759d569955c51a9b80f5c98b643a1993e0a03ee7b7b017caee5d6624b0e79fe"
			}
			locale={locale}
			locales={locales}
		>
			{jsx(stdin_default2, {
				...rest,
			})}
		</ContextProviders>
	);
}

/**
 * @type {import("unframer").UnframerBreakpoint}
 * Unframer에서 사용하는 반응형 브레이크포인트 타입
 */

/**
 * @typedef VariantsMap
 * UnframerBreakpoint별 variant 맵 타입 정의.
 * - base 키는 필수, 나머지 브레이크포인트 키는 선택
 * { [key in UnframerBreakpoint]?: Props['variant'] } & { base: Props['variant'] }
 */

/**
 * 반응형 컴포넌트:
 * - 브레이크포인트별로 다른 variant를 적용하여 KangStay 컴포넌트를 렌더링
 * - variants prop에서 브레이크포인트별 variant 맵을 받음
 */
ComponentWithRoot.Responsive = ({ locale = "", ...rest }) => {
	return (
		<ContextProviders
			routes={routes}
			framerSiteId={
				"3759d569955c51a9b80f5c98b643a1993e0a03ee7b7b017caee5d6624b0e79fe"
			}
			locale={locale}
			locales={locales}
		>
			<WithFramerBreakpoints
				Component={stdin_default2}
				variants={defaultResponsiveVariants}
				{...rest}
			/>
		</ContextProviders>
	);
};

